import React, { useState } from 'react';
import './Login.css';
import bgImage from './images/cafeteria-photo.jpg';

export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showWarning, setShowWarning] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (username.trim() === '' || password.trim() === '') {
      setShowWarning(true);
    } else {
      // perform login logic here
    }
  };

  return (
    <div className='login-container'>
      <h1>Welcome to the website of UNT Cafeteria.</h1>

      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          {showWarning && <p>Please enter a username and password</p>}
          <label htmlFor="username">Username:</label>
          <input type="text" name="username" placeholder="Enter username" value={username} onChange={(e) => setUsername(e.target.value)} />

          <label htmlFor="password">Password:</label>
          <input type="password" name="password" placeholder="Enter password" value={password} onChange={(e) => setPassword(e.target.value)} />

          <input type="submit" value="Login" />
        </form>
      </div>
    </div>
  );
}
