import React from 'react'
import { db } from '../firebase.js'
import { doc, setDoc } from "firebase/firestore"



export default function AdminAddMenu() {
    const [category, setCategory] = useState('');
    const Submit = async(event) => {
        event.preventDefault()
        const itemName = event.target[0].value;
        const price = event.target[1].value;
        console.log("Updated")

        await setDoc(doc(db, "Menu", "HomeMenu"), {
            Name: itemName,
            Price: price
        });
        console.log("Updated")


    }
    return (
        <div>
            <form onSubmit={Submit}>
                <label htmlFor="itemName">Item Name:</label>
                <input type="text" id="itemName" className="itemName" />
                <label htmlFor="price">Price:</label>
                <label htmlFor="itemName" className="name">Categories:</label>
                <div className = "categories">
         <select id="category" value={category}> 
             <option value="" className="categoryVal">Select a category</option>
             <option value="grocery" className="option">Grocery</option> 
             <option value="education" className="option">Education</option>
             <option value="food" className="option">Food</option>
             <option value="rent" className="option">Rent</option>
             <option value="fuel" className="option">Fuel</option>
         </select>
         </div>
                <input type="text" id="price" className="price" />
                <input type="submit" value="Submit" />
            </form>
        </div>
    )
}
